Sprite *Sprites::_sprite[Sprites::num_sprites];
M5Canvas *Sprites::_canvas = nullptr;
FFMap *Sprites::_map = nullptr;

Sprites::Sprites()
{
    _canvas = new M5Canvas(&M5.Display);
    _canvas->createSprite(160, 96);
    _canvas->setColorDepth(8);
    for (int i = 0 ; i < num_sprites ; i++)
    {
        _sprite[i] = new Sprite(*_canvas, _sprite_data[i]);
    }
    _map = new FFMap();
}

Sprites::~Sprites()
{
    delete _map;
    for (int i = 0 ; i < num_sprites ; i++)
    {
        delete _sprite[i];
    }
    delete _canvas;
}

